import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

function App() {
  const [users, setUsers] = useState([]);
  const [name, setName] = useState("");
  const [editId, setEditId] = useState(null); // holds the id of the user being edited

  const handleSubmit = (e) => {
    e.preventDefault();

    if (name.trim() === "") {
      alert("Please enter a name");
      return;
    }

    if (editId) {
      // If editing existing user
      const updatedUsers = users.map((user) =>
        user.id === editId ? { ...user, name } : user
      );
      setUsers(updatedUsers);
      setEditId(null);
    } else {
      // If adding a new user
      const newUser = { id: Date.now(), name: name };
      setUsers([...users, newUser]);
    }

    setName(""); 
  };

  const handleDelete = (id) => {
    setUsers(users.filter((user) => user.id !== id));
  };

  const handleEdit = (user) => {
    setEditId(user.id);
    setName(user.name);
  };

  return (
    <div className="container mt-5 text-center">
      <h2 className="mb-4 text-primary">User List</h2>

      <form onSubmit={handleSubmit} className="mb-4">
        <input
          type="text"
          className="form-control mb-3"
          placeholder="Enter user name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />

        <button type="submit" className="btn btn-success">
          {editId ? "Update User" : "Add User"}
        </button>
        {editId && (
          <button
            type="button"
            className="btn btn-secondary ms-2"
            onClick={() => {
              setEditId(null);
              setName("");
            }}
          >
            Cancel
          </button>
        )}
      </form>

      {users.length === 0 ? (
        <h5 className="text-muted">No users added yet</h5>
      ) : (
        <ul className="list-group">
          {users.map((user) => (
            <li
              key={user.id}
              className="list-group-item d-flex justify-content-between align-items-center"
            >
              {user.name}
              <div>
                <button
                  onClick={() => handleEdit(user)}
                  className="btn btn-sm btn-warning me-2"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(user.id)}
                  className="btn btn-sm btn-danger"
                >
                  Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
